import{_ as e,c as l,o as n,a2 as d}from"./chunks/framework.Dm7eAID-.js";const o=JSON.parse('{"title":"uroboroSQLについて","description":"","frontmatter":{"head":[["meta",{"name":"og:title","content":"uroboroSQLについて"}],["meta",{"name":"og:url","content":"/uroborosql-doc_v0.x/about/"}]]},"headers":[],"relativePath":"about/index.md","filePath":"about/index.md"}'),r={name:"about/index.md"};function a(i,t,s,g,y,x){return n(),l("div",null,t[0]||(t[0]=[d('<h1 id="uroborosqlについて" tabindex="-1">uroboroSQLについて <a class="header-anchor" href="#uroborosqlについて" aria-label="Permalink to &quot;uroboroSQLについて&quot;">​</a></h1><h2 id="github" tabindex="-1">GitHub <a class="header-anchor" href="#github" aria-label="Permalink to &quot;GitHub&quot;">​</a></h2><p><a href="https://github.com/future-architect/uroborosql" target="_blank" rel="noreferrer">https://github.com/future-architect/uroborosql</a></p><h2 id="リリースノート" tabindex="-1">リリースノート <a class="header-anchor" href="#リリースノート" aria-label="Permalink to &quot;リリースノート&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:center;">バージョン</th><th style="text-align:center;">リリース日</th><th style="text-align:left;">概要</th></tr></thead><tbody><tr><td style="text-align:center;">v0.26.9</td><td style="text-align:center;">2025/05/05</td><td style="text-align:left;">updateChainedメソッド追加</td></tr><tr><td style="text-align:center;">v0.26.8</td><td style="text-align:center;">2024/05/15</td><td style="text-align:left;">merge または mergeAndReturn で更新するEntityに@Versionタグが付与されていた場合に楽観ロックエラーが発生する不具合を修正</td></tr><tr><td style="text-align:center;">v0.26.7</td><td style="text-align:center;">2023/11/06</td><td style="text-align:left;">SqlEntityQuery#sum でOptional型に対応。またSQL発行前のカンマやAND/ORの除去処理のパフォーマンスチューニングを実施</td></tr><tr><td style="text-align:center;">v0.26.6</td><td style="text-align:center;">2023/06/05</td><td style="text-align:left;">複数の種類の違うDBに接続する設定の場合にSFファンクションで接続したDBと別のDialectの情報が利用される不具合を修正</td></tr><tr><td style="text-align:center;">v0.26.5</td><td style="text-align:center;">2023/02/16</td><td style="text-align:left;">SqlConfig構築時に設定したDefaultMaxRetryCountを個別のSQL発行時にOFFにする目的でSqlQuery#retry(0) としても、個別指定が適用されずDefaultMaxRetryCountが有効になっていた不具合を修正</td></tr><tr><td style="text-align:center;">v0.26.4</td><td style="text-align:center;">2023/02/02</td><td style="text-align:left;">Entity APIで timestamp型、または timestamp with timezone型のカラムを使った楽観ロックに対応</td></tr><tr><td style="text-align:center;">v0.26.3</td><td style="text-align:center;">2022/12/12</td><td style="text-align:left;">バッチ処理を行う際に生成するMapについて、initialCapacityを適切に指定することでMapのresizeを抑止し処理を高速化。またログ出力の条件判定を追加し不要な文字列加工が発生しないように修正</td></tr><tr><td style="text-align:center;">v0.26.2</td><td style="text-align:center;">2022/11/23</td><td style="text-align:left;">PostgresqlでSqlAgent#batch() 処理が遅い問題を解消</td></tr><tr><td style="text-align:center;">v0.26.1</td><td style="text-align:center;">2022/11/16</td><td style="text-align:left;">Connectionをクローズする際、そのコネクションがクローズ済みかどうかを判定する処理を追加</td></tr><tr><td style="text-align:center;">v0.26.0</td><td style="text-align:center;">2022/10/12</td><td style="text-align:left;">SqlContextに、特定の条件下でSQL更新処理を回避する必要がある場合に、更新デリゲート処理を指定するAPIを追加</td></tr><tr><td style="text-align:center;">v0.25.2</td><td style="text-align:center;">2022/09/14</td><td style="text-align:left;">DefaultEntityHandlerとMappingUtilsの内部で保持するキャッシュを外部から明示的にクリアするためのメソッドを追加</td></tr><tr><td style="text-align:center;">v0.25.1</td><td style="text-align:center;">2022/08/30</td><td style="text-align:left;">DAOインタフェースで生成するSQLにテーブル名のみを出力するか、スキーマ名で修飾したテーブル名を出力するかを指定するオプション(<code>uroborosql.use.qualified.table.name</code>)を追加</td></tr><tr><td style="text-align:center;">v0.25.0</td><td style="text-align:center;">2022/08/21</td><td style="text-align:left;">DAOインタフェースに <code>select</code> メソッドを追加。<br>また collect/first/findFirst/one/findOne/stream の型指定メソッドで単項目の型（StringやLongなど）を指定できるように機能を拡張<br>postgresqlでsearch_pathを使用して複数のスキーマにアクセスする場合、DAO インタフェースで生成されるSQLのスキーマ名が不正となる不具合の修正</td></tr><tr><td style="text-align:center;">v0.24.0</td><td style="text-align:center;">2022/05/22</td><td style="text-align:left;">DAOインタフェースで挿入を行う際、デフォルト値の指定があるカラムに対するフィールドが <code>null</code> の場合、デフォルト値が設定されるように動作を変更</td></tr><tr><td style="text-align:center;">v0.23.1</td><td style="text-align:center;">2022/05/13</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.23.0</td><td style="text-align:center;">2022/04/11</td><td style="text-align:left;">SqlEntityQuery#betweenColumns / SqlEntityQuery#includeColumns / SqlEntityQuery#excludeColumns の追加</td></tr><tr><td style="text-align:center;">v0.22.0</td><td style="text-align:center;">2022/03/22</td><td style="text-align:left;">SqlAgent#merge / SqlAgent#mergeAndReturn / SqlAgent#mergeWithLocking SqlAgent#mergeWithLockingAndReturn の追加</td></tr><tr><td style="text-align:center;">v0.21.1</td><td style="text-align:center;">2022/01/07</td><td style="text-align:left;">SqlAgent#autoCommitScopeの追加</td></tr><tr><td style="text-align:center;">v0.21.0</td><td style="text-align:center;">2022/01/02</td><td style="text-align:left;">ResultSetConverterの性能改善 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.5</td><td style="text-align:center;">2021/12/19</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.4</td><td style="text-align:center;">2021/11/25</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.3</td><td style="text-align:center;">2021/03/15</td><td style="text-align:left;">SqlManagerで複数のルートフォルダ指定が可能に / バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.2</td><td style="text-align:center;">2020/10/29</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.1</td><td style="text-align:center;">2020/09/17</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.20.0</td><td style="text-align:center;">2020/08/25</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.19.0</td><td style="text-align:center;">2020/05/17</td><td style="text-align:left;">日付・時刻を文字列型のカラムで保持する場合のマッパーを追加 / 複数DB接続を考慮したAPIの改善 / InsertsTypeのデフォルトをBATCHへ変更</td></tr><tr><td style="text-align:center;">v0.18.2</td><td style="text-align:center;">2020/03/29</td><td style="text-align:left;">悲観的ロック例外時に、SQLExceptionの代わりに専用のPessimisticLockExceptionをスローするように変更</td></tr><tr><td style="text-align:center;">v0.17.1</td><td style="text-align:center;">2020/03/16</td><td style="text-align:left;">バグフィックス (v0.18.1 の修正を v0.17.0へバックポート)</td></tr><tr><td style="text-align:center;">v0.18.1</td><td style="text-align:center;">2020/03/15</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.18.0</td><td style="text-align:center;">2020/03/08</td><td style="text-align:left;">SqlAgent#savepointScopeメソッド追加 / SqlEntityQuery#selectメソッド, hintメソッド追加 / 式言語としてSpring Expression Language(SpEL)を利用可能に / バグフィックス</td></tr><tr><td style="text-align:center;">v0.17.0</td><td style="text-align:center;">2019/12/22</td><td style="text-align:left;">SqlAgent#truncateメソッドを追加</td></tr><tr><td style="text-align:center;">v0.16.0</td><td style="text-align:center;">2019/12/13</td><td style="text-align:left;">@VersionアノテーションでOptimisticLockSupplierを指定可能に / バグフィックス</td></tr><tr><td style="text-align:center;">v0.15.2</td><td style="text-align:center;">2019/12/13</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.15.1</td><td style="text-align:center;">2019/10/16</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.15.0</td><td style="text-align:center;">2019/10/11</td><td style="text-align:left;">SqlEntityUpdateによる条件指定の複数更新サポート / SqlAgent#updates, SqlAgent#updatesAndReturnの追加による一括更新追加 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.14.0</td><td style="text-align:center;">2019/09/17</td><td style="text-align:left;">SqlEntityQueryで悲観ロックのサポート / SqlQuery#one,findOneの追加 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.13.0</td><td style="text-align:center;">2019/04/26</td><td style="text-align:left;">REPLで使用しているjLineのバージョンを v2 から v3に変更。また依存関係をオプションに変更 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.12.1</td><td style="text-align:center;">2019/04/12</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.12.0</td><td style="text-align:center;">2019/04/09</td><td style="text-align:left;">自動採番カラムを持つエンティティの挿入に対応 / エンティティによる検索で集約関数を提供 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.11.1</td><td style="text-align:center;">2019/03/12</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.11.0</td><td style="text-align:center;">2019/03/11</td><td style="text-align:left;">エンティティによる検索で抽出条件の指定を可能に</td></tr><tr><td style="text-align:center;">v0.10.1</td><td style="text-align:center;">2019/02/07</td><td style="text-align:left;">SqlFluentにsqlIdとparam(String, Supplier)を追加 / バグフィックス</td></tr><tr><td style="text-align:center;">v0.10.0</td><td style="text-align:center;">2019/01/16</td><td style="text-align:left;">SqlAgent#insertsによる一括INSERT / バグフィックス</td></tr><tr><td style="text-align:center;">v0.9.0</td><td style="text-align:center;">2019/01/07</td><td style="text-align:left;">NioSqlManagerのファイル監視を設定可能に / バグフィックス</td></tr><tr><td style="text-align:center;">v0.8.2</td><td style="text-align:center;">2018/05/26</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.8.1</td><td style="text-align:center;">2018/04/26</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.8.0</td><td style="text-align:center;">2018/04/16</td><td style="text-align:left;">DialectによるDB製品対応 / CLOB/BLOB対応 / NioSqlMager追加 / etc.</td></tr><tr><td style="text-align:center;">v0.7.0</td><td style="text-align:center;">2018/03/10</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.6.1</td><td style="text-align:center;">2018/02/05</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.6.0</td><td style="text-align:center;">2018/01/31</td><td style="text-align:left;">自動パラメータバインド関数 API追加　/ バグフィックス</td></tr><tr><td style="text-align:center;">v0.5.0</td><td style="text-align:center;">2017/12/13</td><td style="text-align:left;">Stream batch API追加　/ SQL内の不要なカンマの除去</td></tr><tr><td style="text-align:center;">v0.4.0</td><td style="text-align:center;">2017/11/17</td><td style="text-align:left;">UroboroSQL ビルダーAPI追加 / CaseFormat初期値設定の追加 / etc.</td></tr><tr><td style="text-align:center;">v0.3.3</td><td style="text-align:center;">2017/10/31</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.3.2</td><td style="text-align:center;">2017/10/10</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.3.1</td><td style="text-align:center;">2017/10/02</td><td style="text-align:left;">バグフィックス</td></tr><tr><td style="text-align:center;">v0.3.0</td><td style="text-align:center;">2017/09/08</td><td style="text-align:left;">リファクタリング API</td></tr><tr><td style="text-align:center;">v0.2.0</td><td style="text-align:center;">2017/05/26</td><td style="text-align:left;">カバレッジレポート拡張　/ SQL-REPL機能拡張 / ORMapper API　追加</td></tr><tr><td style="text-align:center;">v0.1.0</td><td style="text-align:center;">2017/03/09</td><td style="text-align:left;">初版リリース</td></tr></tbody></table><p>See more info. <a href="https://github.com/future-architect/uroborosql/releases" target="_blank" rel="noreferrer">Github releases</a></p>',6)]))}const f=e(r,[["render",a]]);export{o as __pageData,f as default};
